import styled from "styled-components";

export const SearchDropDownWrapper = styled.ul`
  text-align: center;
  width: auto;
  z-index: 100;
  font-weight: bold;
  list-style: none;
  position: absolute;
`;

export const DropDownItem = styled.li`
  border: 0.5px solid black;
  font-size: 25px;
  font-weight: 800;
  margin: 10px 0;
  width: 260px;
  background-color: #00FF00;
  margin: 0 auto;
  height: 40px;
`;
